$(document).ready(function(){$("a[href*=#]").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var a=$(this.hash);if(a=a.length&&a||$("[name="+this.hash.slice(1)+"]"),a.length){var e=a.offset().top;return $("html,body").animate({scrollTop:e},1e3),!1}}})});